package Array;

public class Pattren_program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 4 ;
		 for (int i = 0; i <= n; i++)
		 {
			 for (int j = 1; j <= n; j++) 
			 {
				 System.out.print(" * ");
			 }
			 
			 System.out.println();
		 }

			

		
	}

}
