package Array;

public class Rectangle_pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l = 7  ;
		int b= 2;
		 for (int i = 0; i <= b; i++)
		 {
			 for (int j = 1; j <= l; j++) 
			 {
				 System.out.print(" * ");
			 }
			 
			 System.out.println();
		 }

	}

}
